(38, 'Miu Lê', '<p><strong style=\"color: #202122; font-family: sans-serif;\">L&ecirc; &Aacute;nh Nhật</strong><span style=\"color: #202122; font-family: sans-serif;\">&nbsp;hay c&ograve;n được kh&aacute;n giả biết đến với nghệ danh l&agrave;&nbsp;</span><strong style=\"color: #202122; font-family: sans-serif;\">Miu L&ecirc;</strong><span style=\"color: #202122; font-family: sans-serif;\">, sinh ng&agrave;y&nbsp;</span><a style=\"text-decoration-line: none; color: #0b0080; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: sans-serif;\" title=\"5 th&aacute;ng 7\" href=\"https://vi.wikipedia.org/wiki/5_th%C3%A1ng_7\">05 th&aacute;ng 07</a><span style=\"color: #202122; font-family: sans-serif;\">&nbsp;năm&nbsp;</span><a style=\"text-decoration-line: none; color: #0b0080; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: sans-serif;\" title=\"1991\" href=\"https://vi.wikipedia.org/wiki/1991\">1991</a><span style=\"color: #202122; font-family: sans-serif;\">&nbsp;tại&nbsp;</span><a style=\"text-decoration-line: none; color: #0b0080; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: sans-serif;\" title=\"Th&agrave;nh phố Hồ Ch&iacute; Minh\" href=\"https://vi.wikipedia.org/wiki/Th%C3%A0nh_ph%E1%BB%91_H%E1%BB%93_Ch%C3%AD_Minh\">Th&agrave;nh phố Hồ Ch&iacute; Minh</a><span style=\"color: #202122; font-family: sans-serif;\">&nbsp;l&agrave; một nữ&nbsp;</span><a style=\"text-decoration-line: none; color: #0b0080; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: sans-serif;\" title=\"Ca sĩ\" href=\"https://vi.wikipedia.org/wiki/Ca_s%C4%A9\">ca sĩ</a><span style=\"color: #202122; font-family: sans-serif;\">,&nbsp;</span><a style=\"text-decoration-line: none; color: #0b0080; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: sans-serif;\" title=\"Diễn vi&ecirc;n\" href=\"https://vi.wikipedia.org/wiki/Di%E1%BB%85n_vi%C3%AAn\">diễn vi&ecirc;n</a><span style=\"color: #202122; font-family: sans-serif;\">&nbsp;người&nbsp;</span><a style=\"text-decoration-line: none; color: #0b0080; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: sans-serif;\" title=\"Việt Nam\" href=\"https://vi.wikipedia.org/wiki/Vi%E1%BB%87t_Nam\">Việt Nam</a><span style=\"color: #202122; font-family: sans-serif;\">.</span></p>', 'singers\\August2020\\EechocBSM84MsSQ0hDzf.jpg', '2020-08-11 20:45:21', '2020-08-11 20:45:21'),
(39, 'Đình Phước', '<p><strong>Đ&igrave;nh Phước</strong> sinh ra v&agrave; lớn l&ecirc; tại qu&ecirc; hương S&ocirc;ng B&eacute; (B&igrave;nh Phước), thừa hưởng chất giọng lai Trung từ cha gốc Nghệ An, mẹ gốc Huế.&nbsp; Từ nhỏ anh đ&atilde; tập t&agrave;nh h&aacute;t nhạc v&agrave;ng do mẹ v&agrave; b&agrave; ngoại thường hay h&aacute;t. Đ&igrave;nh Phước biết h&aacute;t &ldquo;N&oacute;i với người t&igrave;nh&ldquo; từ khi chưa biết đọc chữ.</p>\r\n<div id=\"gtx-trans\" style=\"position: absolute; left: -1px; top: 32px;\">\r\n<div class=\"gtx-trans-icon\">&nbsp;</div>\r\n</div>', 'singers\\August2020\\Yu0Dl0U5I2hM1aQH9GI3.jpg', '2020-08-11 21:19:03', '2020-08-11 21:19:03'),
(40, 'Đoàn Minh', '<p><span style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">T&ecirc;n thật&nbsp;</span><strong style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">Đo&agrave;n</strong><span style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\"> V<strong>ăn&nbsp;</strong></span><strong style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">Minh</strong><span style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">, sinh năm 1979, qu&ecirc; gốc Chợ L&aacute;ch, Bến Tre, mất cha khi mới vừa l&ecirc;n lớp 9,&nbsp;</span><strong style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">L&agrave;</strong><span style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">&nbsp;người con thứ 2 trong gia đ&igrave;nh 4 anh em. hết lớp 11, do chuyện mưu sinh của gia đ&igrave;nh, anh chuyển l&ecirc;n S&agrave;i G&ograve;n vừa học văn h&oacute;a vừa học khoa &acirc;m nhạc trường Trung cấp Thanh Nhạc.</span></p>', 'singers\\August2020\\Fq7vxJWkR6MaoSfUeXc2.jpg', '2020-08-11 21:25:00', '2020-08-11 21:26:06'),
(41, 'Khôi Nguyên', '<p><strong style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">Kh&ocirc;i Nguy&ecirc;n</strong><span style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">&nbsp;t&ecirc;n thật l&agrave; Trang Nhật Khởi, sinh ng&agrave;y 29 th&aacute;ng 1 tại Tr&agrave; Vinh - miền đất sản sinh ra kh&aacute; nhiều&nbsp;</span><strong style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">nhạc sĩ</strong><span style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">,&nbsp;</span><strong style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">ca sĩ</strong><span style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">, trong đ&oacute; c&oacute;&nbsp;</span><strong style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">nhạc sĩ</strong><span style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">&nbsp;Tr&uacute;c Phương - &ocirc;ng ho&agrave;ng&nbsp;</span><strong style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">của</strong><span style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">&nbsp;d&ograve;ng&nbsp;</span><strong style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">nhạc</strong><span style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">&nbsp;bolero...&nbsp;</span><strong style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">Kh&ocirc;i Nguy&ecirc;n</strong><span style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">&nbsp;v&agrave;o nghề&nbsp;</span><strong style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">ca</strong><span style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">&nbsp;h&aacute;t với&nbsp;</span><strong style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">sự</strong><span style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">&nbsp;d&igrave;u dắt&nbsp;</span><strong style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">của</strong><span style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">&nbsp;người thầy, người ch&uacute; l&agrave;&nbsp;</span><strong style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">nhạc sĩ</strong><span style=\"color: #222222; font-family: Roboto, Arial, sans-serif; font-size: 16px;\">&nbsp;Thanh Sơn - t&aacute;c giả Nỗi Buồn Hoa Phượng.</span></p>', 'singers\\August2020\\BCO9hwIf7wT7HO2O25hE.jpg', '2020-08-11 23:30:58', '2020-08-11 23:30:58'),
(42, 'Lam Anh', '<p><span style=\"color: #4d5156; font-family: Roboto, Arial, sans-serif;\"><strong>Lam Anh</strong> l&agrave; một nữ ca sĩ hải ngoại. Hiện c&ocirc; đang cộng t&aacute;c với Trung t&acirc;m Th&uacute;y Nga.</span></p>\r\n<div id=\"gtx-trans\" style=\"position: absolute; left: -38px; top: 32px;\">\r\n<div class=\"gtx-trans-icon\">&nbsp;</div>\r\n</div>', 'singers\\August2020\\aCC6QeNbn53ZLJ6WSZWF.jpg', '2020-08-11 23:46:11', '2020-08-11 23:46:11'),
(43, 'Lâm Bảo Phi', '<p><span style=\"color: #2e3338; font-family: Whitney, \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 16px; white-space: break-spaces;\">Ca sĩ L&acirc;m Bảo Phi sinh tại Tỉnh Thanh H&oacute;a. Anh sống v&agrave; l&agrave;m việc chủ yếu ở Th&agrave;nh phố Hồ Ch&iacute; Minh.- L&acirc;m Bảo Phi l&agrave; ca sĩ kh&aacute; th&agrave;nh c&ocirc;ng với d&ograve;ng nhạc trữ t&igrave;nh s&acirc;u lắng được nhiều kh&aacute;n giả quan t&acirc;m v&agrave; y&ecirc;u mến tại Việt Nam. Với chất giọng trữ tr&igrave;nh thi&ecirc;n ph&uacute; c&ugrave;ng với gương mặt kh&aacute; điển trai tr&ecirc;n s&acirc;n khấu L&acirc;m Bảo Phi nhanh ch&oacute;ng được kh&aacute;n th&iacute;nh giả gần xa trong v&agrave; ngo&agrave;i nước biết đến. Với l&ograve;ng hảo t&acirc;m của m&igrave;nh, L&acirc;m Bảo Phi c&ograve;n được người h&acirc;m mộ biết đến bởi c&aacute;c chương tr&igrave;nh ca nhạc từ thiện m&agrave; anh tham gia.</span></p>\r\n<div id=\"gtx-trans\" style=\"position: absolute; left: 130px; top: -14px;\">\r\n<div class=\"gtx-trans-icon\">&nbsp;</div>\r\n</div>', 'singers\\August2020\\awP9l6VXpuw7xtuSFUZb.jpg', '2020-08-11 23:51:32', '2020-08-11 23:51:32'),
(44, 'Mỹ Tâm', '<p><strong style=\"color: #202122; font-family: sans-serif;\">Phan Thị Mỹ T&acirc;m</strong><span style=\"color: #202122; font-family: sans-serif;\">&nbsp;(sinh ng&agrave;y&nbsp;</span><a style=\"text-decoration-line: none; color: #0b0080; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: sans-serif;\" title=\"16 th&aacute;ng 1\" href=\"https://vi.wikipedia.org/wiki/16_th%C3%A1ng_1\">16 th&aacute;ng 1</a><span style=\"color: #202122; font-family: sans-serif;\">&nbsp;năm&nbsp;</span><a style=\"text-decoration-line: none; color: #0b0080; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: sans-serif;\" title=\"1981\" href=\"https://vi.wikipedia.org/wiki/1981\">1981</a><span style=\"color: #202122; font-family: sans-serif;\">) l&agrave; nữ&nbsp;</span><a style=\"text-decoration-line: none; color: #0b0080; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: sans-serif;\" title=\"Ca sĩ\" href=\"https://vi.wikipedia.org/wiki/Ca_s%C4%A9\">ca sĩ</a><span style=\"color: #202122; font-family: sans-serif;\">,&nbsp;</span><a style=\"text-decoration-line: none; color: #0b0080; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: sans-serif;\" title=\"Nhạc sĩ\" href=\"https://vi.wikipedia.org/wiki/Nh%E1%BA%A1c_s%C4%A9\">nhạc sĩ</a><span style=\"color: #202122; font-family: sans-serif;\">&nbsp;v&agrave;&nbsp;</span><a style=\"text-decoration-line: none; co